import { motion } from "framer-motion";
import { Plus, Calendar, Clock, Trash2, Edit, DollarSign } from "lucide-react";
import DashboardLayout from "@/components/layouts/DashboardLayout";
import { useState, useEffect } from "react";
import { useToast } from "@/hooks/use-toast";
import { useQuery } from "@apollo/client";
import { GET_CATEGORIES } from "@/graphql/serviceQueries";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
} from "@/components/ui/tabs";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";

type Service = {
  id: number;
  title: string;
  description: string;
  price: number;
  duration_minutes: number;
  is_active: boolean;
  category_id: number;
  category_name: string;
};

type AvailabilitySlot = {
  id: number;
  vendorId: number;
  serviceId?: number;
  serviceName?: string;
  slotDate: string;
  startTime: string;
  endTime: string;
  isAvailable: boolean;
};

// Standard time slots (30-minute intervals)
const TIME_SLOTS = [
  { start: "09:00", end: "09:30", label: "9:00 AM" },
  { start: "09:30", end: "10:00", label: "9:30 AM" },
  { start: "10:00", end: "10:30", label: "10:00 AM" },
  { start: "10:30", end: "11:00", label: "10:30 AM" },
  { start: "11:00", end: "11:30", label: "11:00 AM" },
  { start: "11:30", end: "12:00", label: "11:30 AM" },
  { start: "12:00", end: "12:30", label: "12:00 PM" },
  { start: "12:30", end: "13:00", label: "12:30 PM" },
  { start: "13:00", end: "13:30", label: "1:00 PM" },
  { start: "13:30", end: "14:00", label: "1:30 PM" },
  { start: "14:00", end: "14:30", label: "2:00 PM" },
  { start: "14:30", end: "15:00", label: "2:30 PM" },
  { start: "15:00", end: "15:30", label: "3:00 PM" },
  { start: "15:30", end: "16:00", label: "3:30 PM" },
  { start: "16:00", end: "16:30", label: "4:00 PM" },
  { start: "16:30", end: "17:00", label: "4:30 PM" },
  { start: "17:00", end: "17:30", label: "5:00 PM" },
  { start: "17:30", end: "18:00", label: "5:30 PM" },
];

export default function VendorServices() {
  const { toast } = useToast();
  
  // Vendor profile state
  const [vendorCategoryId, setVendorCategoryId] = useState<number | null>(null);
  const [vendorCategoryName, setVendorCategoryName] = useState<string | null>(null);
  
  // Services state
  const [services, setServices] = useState<Service[]>([]);
  const [loadingServices, setLoadingServices] = useState(false);
  const [showAddServiceDialog, setShowAddServiceDialog] = useState(false);
  const [editingService, setEditingService] = useState<Service | null>(null);
  
  // Service form state
  const [serviceTitle, setServiceTitle] = useState("");
  const [serviceDescription, setServiceDescription] = useState("");
  const [servicePrice, setServicePrice] = useState("");
  const [serviceDuration, setServiceDuration] = useState("");
  
  // Availability slots state
  const [slots, setSlots] = useState<AvailabilitySlot[]>([]);
  const [loadingSlots, setLoadingSlots] = useState(false);
  const [showAddSlotDialog, setShowAddSlotDialog] = useState(false);
  const [slotDate, setSlotDate] = useState("");
  const [slotServiceId, setSlotServiceId] = useState(""); // Service for manual slot creation
  const [selectedTimeSlots, setSelectedTimeSlots] = useState<string[]>([]); // Store "start-end" strings
  const [submitting, setSubmitting] = useState(false);

  // Auto-generate slots state
  const [showAutoGenerateDialog, setShowAutoGenerateDialog] = useState(false);
  const [generateServiceId, setGenerateServiceId] = useState("");
  const [generateStartDate, setGenerateStartDate] = useState("");
  const [generateEndDate, setGenerateEndDate] = useState("");
  const [generateStartTime, setGenerateStartTime] = useState("09:00");
  const [generateEndTime, setGenerateEndTime] = useState("17:00");
  const [generating, setGenerating] = useState(false);

  const { data: categoriesData } = useQuery(GET_CATEGORIES);
  const categories = (categoriesData?.categories ?? []) as Array<{ id: string; name: string }>;

  useEffect(() => {
    fetchVendorProfile();
    fetchServices();
    fetchSlots();
  }, []);

  const fetchVendorProfile = async () => {
    try {
      const response = await fetch("/auth/profile", {
        credentials: "include",
      });
      const result = await response.json();

      if (result.ok && result.profile.vendor) {
        setVendorCategoryId(result.profile.vendor.serviceCategoryId);
        // Find category name
        const category = categories.find(c => c.id === result.profile.vendor.serviceCategoryId?.toString());
        setVendorCategoryName(category?.name || null);
      }
    } catch (err) {
      console.error("Failed to fetch vendor profile:", err);
    }
  };

  // Update category name when categories load
  useEffect(() => {
    if (vendorCategoryId && categories.length > 0) {
      const category = categories.find(c => c.id === vendorCategoryId.toString());
      setVendorCategoryName(category?.name || null);
    }
  }, [vendorCategoryId, categories]);

  const fetchServices = async () => {
    setLoadingServices(true);
    try {
      const response = await fetch("/api/vendor/services", {
        credentials: "include",
      });
      const result = await response.json();

      if (result.ok) {
        setServices(result.services);
      }
    } catch (err) {
      console.error("Failed to fetch services:", err);
    } finally {
      setLoadingServices(false);
    }
  };

  const handleAddService = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!serviceTitle) {
      toast({
        title: "Missing Fields",
        description: "Please provide a service title.",
        variant: "destructive",
      });
      return;
    }

    if (!vendorCategoryId) {
      toast({
        title: "Category Not Set",
        description: "Please set your service category in your profile first.",
        variant: "destructive",
      });
      return;
    }

    setSubmitting(true);
    try {
      const response = await fetch("/api/vendor/services", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          title: serviceTitle,
          description: serviceDescription,
          price: servicePrice ? parseFloat(servicePrice) : null,
          durationMinutes: serviceDuration ? parseInt(serviceDuration) : null,
          categoryId: vendorCategoryId,
        }),
      });

      const result = await response.json();

      if (result.ok) {
        toast({
          title: "Success",
          description: "Service created successfully!",
        });
        setShowAddServiceDialog(false);
        resetServiceForm();
        fetchServices();
      } else {
        toast({
          title: "Error",
          description: result.error || "Failed to create service.",
          variant: "destructive",
        });
      }
    } catch (err) {
      toast({
        title: "Error",
        description: "Failed to create service.",
        variant: "destructive",
      });
    } finally {
      setSubmitting(false);
    }
  };

  const handleUpdateService = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingService) return;

    setSubmitting(true);
    try {
      const response = await fetch(`/api/vendor/services/${editingService.id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          title: serviceTitle,
          description: serviceDescription,
          price: servicePrice ? parseFloat(servicePrice) : null,
          durationMinutes: serviceDuration ? parseInt(serviceDuration) : null,
          categoryId: vendorCategoryId,
        }),
      });

      const result = await response.json();

      if (result.ok) {
        toast({
          title: "Success",
          description: "Service updated successfully!",
        });
        setEditingService(null);
        resetServiceForm();
        fetchServices();
      } else {
        toast({
          title: "Error",
          description: result.error || "Failed to update service.",
          variant: "destructive",
        });
      }
    } catch (err) {
      toast({
        title: "Error",
        description: "Failed to update service.",
        variant: "destructive",
      });
    } finally {
      setSubmitting(false);
    }
  };

  const handleDeleteService = async (serviceId: number) => {
    if (!confirm("Are you sure you want to delete this service?")) return;

    try {
      const response = await fetch(`/api/vendor/services/${serviceId}`, {
        method: "DELETE",
        credentials: "include",
      });

      const result = await response.json();

      if (result.ok) {
        toast({
          title: "Success",
          description: "Service deleted successfully!",
        });
        fetchServices();
      } else {
        toast({
          title: "Error",
          description: result.error || "Failed to delete service.",
          variant: "destructive",
        });
      }
    } catch (err) {
      toast({
        title: "Error",
        description: "Failed to delete service.",
        variant: "destructive",
      });
    }
  };

  const handleToggleServiceActive = async (service: Service) => {
    try {
      const response = await fetch(`/api/vendor/services/${service.id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          isActive: !service.is_active,
        }),
      });

      const result = await response.json();

      if (result.ok) {
        toast({
          title: "Success",
          description: `Service ${!service.is_active ? "activated" : "deactivated"} successfully!`,
        });
        fetchServices();
      } else {
        toast({
          title: "Error",
          description: result.error || "Failed to update service.",
          variant: "destructive",
        });
      }
    } catch (err) {
      toast({
        title: "Error",
        description: "Failed to update service.",
        variant: "destructive",
      });
    }
  };

  const openEditDialog = (service: Service) => {
    setEditingService(service);
    setServiceTitle(service.title);
    setServiceDescription(service.description || "");
    setServicePrice(service.price?.toString() || "");
    setServiceDuration(service.duration_minutes?.toString() || "");
  };

  const resetServiceForm = () => {
    setServiceTitle("");
    setServiceDescription("");
    setServicePrice("");
    setServiceDuration("");
  };

  const fetchSlots = async () => {
    setLoadingSlots(true);
    try {
      const response = await fetch("/api/availability", {
        credentials: "include",
      });
      const result = await response.json();

      if (result.ok) {
        console.log("Slots fetched:", result.slots);
        setSlots(result.slots);
      } else {
        console.error("Failed to fetch slots:", result.error);
      }
    } catch (err) {
      console.error("Failed to fetch slots:", err);
    } finally {
      setLoadingSlots(false);
    }
  };

  const handleAddSlot = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!slotDate) {
      toast({
        title: "Missing Date",
        description: "Please select a date.",
        variant: "destructive",
      });
      return;
    }

    if (selectedTimeSlots.length === 0) {
      toast({
        title: "No Time Slots Selected",
        description: "Please select at least one time slot.",
        variant: "destructive",
      });
      return;
    }

    setSubmitting(true);
    try {
      // Create all selected slots
      const promises = selectedTimeSlots.map(slotKey => {
        const [startTime, endTime] = slotKey.split('-');
        return fetch("/api/availability", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          credentials: "include",
          body: JSON.stringify({
            serviceId: slotServiceId ? Number(slotServiceId) : undefined,
            slotDate,
            startTime,
            endTime,
          }),
        });
      });

      const responses = await Promise.all(promises);
      const results = await Promise.all(responses.map(r => r.json()));

      const failedSlots = results.filter(r => !r.ok);
      
      if (failedSlots.length === 0) {
        toast({
          title: "Success",
          description: `${selectedTimeSlots.length} availability slot(s) created successfully.`,
        });
        setShowAddSlotDialog(false);
        setSlotDate("");
        setSlotServiceId("");
        setSelectedTimeSlots([]);
        fetchSlots();
      } else {
        toast({
          title: "Partial Success",
          description: `${results.length - failedSlots.length} slot(s) created, ${failedSlots.length} failed.`,
          variant: "destructive",
        });
        fetchSlots();
      }
    } catch (err) {
      toast({
        title: "Error",
        description: "An error occurred while creating the slots.",
        variant: "destructive",
      });
    } finally {
      setSubmitting(false);
    }
  };

  const handleAutoGenerateSlots = async (e: React.FormEvent) => {
    e.preventDefault();
    setGenerating(true);

    try {
      const response = await fetch("/api/availability/generate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          serviceId: Number(generateServiceId),
          startDate: generateStartDate,
          endDate: generateEndDate,
          workingStartTime: generateStartTime,
          workingEndTime: generateEndTime,
        }),
      });

      const result = await response.json();

      if (result.ok) {
        toast({
          title: "Slots Generated!",
          description: result.message || `${result.slotsGenerated} time slots created.`,
        });
        setShowAutoGenerateDialog(false);
        setGenerateServiceId("");
        setGenerateStartDate("");
        setGenerateEndDate("");
        setGenerateStartTime("09:00");
        setGenerateEndTime("17:00");
        fetchSlots();
      } else {
        toast({
          title: "Error",
          description: result.error || "Failed to generate slots.",
          variant: "destructive",
        });
      }
    } catch (err) {
      toast({
        title: "Error",
        description: "An error occurred while generating slots.",
        variant: "destructive",
      });
    } finally {
      setGenerating(false);
    }
  };

  const toggleTimeSlot = (slotKey: string) => {
    setSelectedTimeSlots(prev => 
      prev.includes(slotKey) 
        ? prev.filter(s => s !== slotKey)
        : [...prev, slotKey]
    );
  };

  const isSlotAlreadyCreated = (startTime: string, endTime: string) => {
    if (!slotDate) return false;
    return slots.some(slot => 
      slot.slotDate === slotDate && 
      slot.startTime === startTime && 
      slot.endTime === endTime
    );
  };

  const handleDeleteSlot = async (slotId: number) => {
    try {
      const response = await fetch(`/api/availability/${slotId}`, {
        method: "DELETE",
        credentials: "include",
      });

      const result = await response.json();

      if (result.ok) {
        toast({
          title: "Success",
          description: "Availability slot deleted successfully.",
        });
        fetchSlots();
      } else {
        toast({
          title: "Error",
          description: result.error || "Failed to delete slot.",
          variant: "destructive",
        });
      }
    } catch (err) {
      toast({
        title: "Error",
        description: "An error occurred while deleting the slot.",
        variant: "destructive",
      });
    }
  };

  const formatDate = (dateStr: string) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString("en-US", { month: "short", day: "numeric", year: "numeric" });
  };

  return (
    <DashboardLayout role="vendor">
      <div className="space-y-6">
        <div>
          <h3 className="font-display font-semibold text-foreground">My Services</h3>
          <p className="text-sm text-muted-foreground mt-1">
            Manage your service offerings and availability schedule
          </p>
        </div>

        <Tabs defaultValue="services" className="w-full">
          <TabsList className="grid w-full max-w-md grid-cols-2">
            <TabsTrigger value="services">Service Catalog</TabsTrigger>
            <TabsTrigger value="availability">Availability Slots</TabsTrigger>
          </TabsList>

          <TabsContent value="services" className="space-y-4 mt-6">
            <div className="flex items-center justify-between">
              <p className="text-sm text-muted-foreground">
                Create and manage the services you offer to customers
              </p>
              <Dialog open={showAddServiceDialog} onOpenChange={setShowAddServiceDialog}>
                <DialogTrigger asChild>
                  <Button className="inline-flex items-center gap-2">
                    <Plus size={16} /> Add Service
                  </Button>
                </DialogTrigger>
                <DialogContent className="max-w-lg">
                  <DialogHeader>
                    <DialogTitle>Add New Service</DialogTitle>
                    <DialogDescription>
                      Create a new service offering for customers to book
                    </DialogDescription>
                  </DialogHeader>
                  <form onSubmit={handleAddService} className="space-y-4">
                    <div className="space-y-2">
                      <Label htmlFor="title">Service Title *</Label>
                      <Input
                        id="title"
                        placeholder="e.g., Professional Haircut"
                        value={serviceTitle}
                        onChange={(e) => setServiceTitle(e.target.value)}
                        required
                      />
                    </div>
                    <div className="space-y-2">
                      <Label>Category</Label>
                      <div className="flex items-center gap-2 px-3 py-2 rounded-md border bg-muted">
                        <Badge variant="secondary">{vendorCategoryName || "Not Set"}</Badge>
                        <span className="text-xs text-muted-foreground">
                          All your services will be in this category
                        </span>
                      </div>
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="description">Description</Label>
                      <Textarea
                        id="description"
                        placeholder="Describe your service..."
                        value={serviceDescription}
                        onChange={(e) => setServiceDescription(e.target.value)}
                        rows={3}
                      />
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <div className="space-y-2">
                        <Label htmlFor="price">Price (â‚¹)</Label>
                        <Input
                          id="price"
                          type="number"
                          step="0.01"
                          placeholder="0.00"
                          value={servicePrice}
                          onChange={(e) => setServicePrice(e.target.value)}
                        />
                      </div>
                      <div className="space-y-2">
                        <Label htmlFor="duration">Duration (min)</Label>
                        <Input
                          id="duration"
                          type="number"
                          placeholder="60"
                          value={serviceDuration}
                          onChange={(e) => setServiceDuration(e.target.value)}
                        />
                      </div>
                    </div>
                    <div className="flex gap-2 justify-end">
                      <Button type="button" variant="outline" onClick={() => setShowAddServiceDialog(false)}>
                        Cancel
                      </Button>
                      <Button type="submit" disabled={submitting}>
                        {submitting ? "Creating..." : "Create Service"}
                      </Button>
                    </div>
                  </form>
                </DialogContent>
              </Dialog>
            </div>

            {loadingServices ? (
              <div className="text-center py-12 text-muted-foreground">Loading services...</div>
            ) : services.length === 0 ? (
              <Card>
                <CardContent className="text-center py-12">
                  <DollarSign size={48} className="mx-auto mb-4 opacity-50 text-muted-foreground" />
                  <p className="text-muted-foreground">No services created yet.</p>
                  <p className="text-xs text-muted-foreground mt-2">Click "Add Service" to create your first offering.</p>
                </CardContent>
              </Card>
            ) : (
              <div className="grid gap-4">
                {services.map((service, i) => (
                  <motion.div
                    key={service.id}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: i * 0.03 }}
                  >
                    <Card>
                      <CardHeader>
                        <div className="flex items-start justify-between">
                          <div className="flex-1">
                            <div className="flex items-center gap-2">
                              <CardTitle className="text-lg">{service.title}</CardTitle>
                              <Badge variant={service.is_active ? "default" : "secondary"}>
                                {service.is_active ? "Active" : "Inactive"}
                              </Badge>
                            </div>
                            <CardDescription className="mt-1">{service.category_name}</CardDescription>
                          </div>
                          <div className="flex items-center gap-2">
                            <Button
                              variant="ghost"
                              size="icon"
                              onClick={() => openEditDialog(service)}
                            >
                              <Edit size={16} />
                            </Button>
                            <Button
                              variant="ghost"
                              size="icon"
                              onClick={() => handleDeleteService(service.id)}
                              className="text-red-400 hover:text-red-300"
                            >
                              <Trash2 size={16} />
                            </Button>
                          </div>
                        </div>
                      </CardHeader>
                      <CardContent className="space-y-3">
                        {service.description && (
                          <p className="text-sm text-muted-foreground">{service.description}</p>
                        )}
                        <div className="flex items-center gap-6 text-sm">
                          {service.price && (
                            <div className="flex items-center gap-1">
                              <DollarSign size={14} className="text-muted-foreground" />
                              <span className="font-medium">â‚¹{service.price}</span>
                            </div>
                          )}
                          {service.duration_minutes && (
                            <div className="flex items-center gap-1">
                              <Clock size={14} className="text-muted-foreground" />
                              <span>{service.duration_minutes} min</span>
                            </div>
                          )}
                        </div>
                        <div className="flex items-center gap-2 pt-2">
                          <Switch
                            checked={service.is_active}
                            onCheckedChange={() => handleToggleServiceActive(service)}
                          />
                          <span className="text-sm text-muted-foreground">
                            {service.is_active ? "Visible to customers" : "Hidden from customers"}
                          </span>
                        </div>
                      </CardContent>
                    </Card>
                  </motion.div>
                ))}
              </div>
            )}

            {/* Edit Service Dialog */}
            <Dialog open={!!editingService} onOpenChange={(open) => !open && setEditingService(null)}>
              <DialogContent className="max-w-lg">
                <DialogHeader>
                  <DialogTitle>Edit Service</DialogTitle>
                  <DialogDescription>
                    Update your service details
                  </DialogDescription>
                </DialogHeader>
                <form onSubmit={handleUpdateService} className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="edit-title">Service Title *</Label>
                    <Input
                      id="edit-title"
                      value={serviceTitle}
                      onChange={(e) => setServiceTitle(e.target.value)}
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label>Category</Label>
                    <div className="flex items-center gap-2 px-3 py-2 rounded-md border bg-muted">
                      <Badge variant="secondary">{vendorCategoryName || "Not Set"}</Badge>
                      <span className="text-xs text-muted-foreground">
                        Category is fixed to your specialization
                      </span>
                    </div>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="edit-description">Description</Label>
                    <Textarea
                      id="edit-description"
                      value={serviceDescription}
                      onChange={(e) => setServiceDescription(e.target.value)}
                      rows={3}
                    />
                  </div>
                  <div className="grid grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="edit-price">Price (â‚¹)</Label>
                      <Input
                        id="edit-price"
                        type="number"
                        step="0.01"
                        value={servicePrice}
                        onChange={(e) => setServicePrice(e.target.value)}
                      />
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="edit-duration">Duration (min)</Label>
                      <Input
                        id="edit-duration"
                        type="number"
                        value={serviceDuration}
                        onChange={(e) => setServiceDuration(e.target.value)}
                      />
                    </div>
                  </div>
                  <div className="flex gap-2 justify-end">
                    <Button type="button" variant="outline" onClick={() => setEditingService(null)}>
                      Cancel
                    </Button>
                    <Button type="submit" disabled={submitting}>
                      {submitting ? "Updating..." : "Update Service"}
                    </Button>
                  </div>
                </form>
              </DialogContent>
            </Dialog>
          </TabsContent>

          <TabsContent value="availability" className="space-y-4 mt-6">
            <div className="flex items-center justify-between">
              <p className="text-sm text-muted-foreground">
                Set your available time slots for customer bookings
              </p>
              <div className="flex gap-2">
                <Dialog open={showAutoGenerateDialog} onOpenChange={setShowAutoGenerateDialog}>
                  <DialogTrigger asChild>
                    <Button variant="outline" className="inline-flex items-center gap-2">
                      <Calendar size={16} /> Auto-Generate
                    </Button>
                  </DialogTrigger>
                  <DialogContent className="max-w-md">
                    <DialogHeader>
                      <DialogTitle>Auto-Generate Time Slots</DialogTitle>
                      <DialogDescription>
                        Automatically create time slots based on service duration with 10-minute breaks
                      </DialogDescription>
                    </DialogHeader>
                    <form onSubmit={handleAutoGenerateSlots} className="space-y-4">
                      <div className="space-y-2">
                        <Label htmlFor="gen-service">Service</Label>
                        <Select value={generateServiceId} onValueChange={setGenerateServiceId} required>
                          <SelectTrigger>
                            <SelectValue placeholder="Select service" />
                          </SelectTrigger>
                          <SelectContent>
                            {services.map((service) => (
                              <SelectItem key={service.id} value={String(service.id)}>
                                {service.title} ({service.duration_minutes} mins)
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>

                      <div className="grid grid-cols-2 gap-4">
                        <div className="space-y-2">
                          <Label htmlFor="gen-start-date">Start Date</Label>
                          <Input
                            id="gen-start-date"
                            type="date"
                            value={generateStartDate}
                            onChange={(e) => setGenerateStartDate(e.target.value)}
                            min={new Date().toISOString().split("T")[0]}
                            required
                          />
                        </div>
                        <div className="space-y-2">
                          <Label htmlFor="gen-end-date">End Date</Label>
                          <Input
                            id="gen-end-date"
                            type="date"
                            value={generateEndDate}
                            onChange={(e) => setGenerateEndDate(e.target.value)}
                            min={generateStartDate || new Date().toISOString().split("T")[0]}
                            required
                          />
                        </div>
                      </div>

                      <div className="grid grid-cols-2 gap-4">
                        <div className="space-y-2">
                          <Label htmlFor="gen-start-time">Start Time</Label>
                          <Input
                            id="gen-start-time"
                            type="time"
                            value={generateStartTime}
                            onChange={(e) => setGenerateStartTime(e.target.value)}
                            required
                          />
                        </div>
                        <div className="space-y-2">
                          <Label htmlFor="gen-end-time">End Time</Label>
                          <Input
                            id="gen-end-time"
                            type="time"
                            value={generateEndTime}
                            onChange={(e) => setGenerateEndTime(e.target.value)}
                            required
                          />
                        </div>
                      </div>

                      <div className="bg-primary/10 border border-primary/30 rounded-lg p-3 text-sm">
                        <p className="text-muted-foreground">
                          ðŸ“… Slots will be created with 10-minute breaks between each service
                        </p>
                      </div>

                      <div className="flex gap-2 justify-end">
                        <Button
                          type="button"
                          variant="outline"
                          onClick={() => setShowAutoGenerateDialog(false)}
                        >
                          Cancel
                        </Button>
                        <Button type="submit" disabled={generating}>
                          {generating ? "Generating..." : "Generate Slots"}
                        </Button>
                      </div>
                    </form>
                  </DialogContent>
                </Dialog>

                <Dialog open={showAddSlotDialog} onOpenChange={setShowAddSlotDialog}>
                  <DialogTrigger asChild>
                    <Button className="inline-flex items-center gap-2">
                      <Plus size={16} /> Add Time Slot
                    </Button>
                  </DialogTrigger>
                <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
                  <DialogHeader>
                    <DialogTitle>Add Availability Slots</DialogTitle>
                    <DialogDescription>
                      Select a date and the time slots you want to make available
                    </DialogDescription>
                  </DialogHeader>
                  <form onSubmit={handleAddSlot} className="space-y-4">
                    <div className="space-y-2">
                      <Label htmlFor="slot-service">Service (Optional)</Label>
                      <Select value={slotServiceId} onValueChange={setSlotServiceId}>
                        <SelectTrigger>
                          <SelectValue placeholder="All services (generic slot)" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="none">All services (generic slot)</SelectItem>
                          {services.map((service) => (
                            <SelectItem key={service.id} value={String(service.id)}>
                              {service.title}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <p className="text-xs text-muted-foreground">
                        Leave unselected to create general availability slots
                      </p>
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="date">Date</Label>
                      <Input
                        id="date"
                        type="date"
                        value={slotDate}
                        onChange={(e) => {
                          setSlotDate(e.target.value);
                          setSelectedTimeSlots([]); // Reset selections when date changes
                        }}
                        min={new Date().toISOString().split("T")[0]}
                        required
                      />
                    </div>

                    {slotDate && (
                      <div className="space-y-2">
                        <Label>Select Time Slots</Label>
                        <div className="grid grid-cols-3 sm:grid-cols-4 gap-2">
                          {TIME_SLOTS.map((slot) => {
                            const slotKey = `${slot.start}-${slot.end}`;
                            const isSelected = selectedTimeSlots.includes(slotKey);
                            const alreadyCreated = isSlotAlreadyCreated(slot.start, slot.end);
                            
                            return (
                              <button
                                key={slotKey}
                                type="button"
                                onClick={() => !alreadyCreated && toggleTimeSlot(slotKey)}
                                disabled={alreadyCreated}
                                className={`px-3 py-2 rounded-lg text-sm font-medium transition-all ${
                                  alreadyCreated
                                    ? "bg-muted text-muted-foreground cursor-not-allowed opacity-50"
                                    : isSelected
                                    ? "bg-primary text-primary-foreground shadow-lg shadow-primary/25"
                                    : "bg-secondary text-foreground hover:bg-secondary/80 border border-border"
                                }`}
                              >
                                {slot.label}
                              </button>
                            );
                          })}
                        </div>
                        <p className="text-xs text-muted-foreground">
                          {selectedTimeSlots.length > 0 
                            ? `${selectedTimeSlots.length} slot(s) selected` 
                            : "Click on time slots to select them"}
                        </p>
                      </div>
                    )}

                    <div className="flex gap-2 justify-end">
                      <Button 
                        type="button" 
                        variant="outline" 
                        onClick={() => {
                          setShowAddSlotDialog(false);
                          setSlotDate("");
                          setSlotServiceId("");
                          setSelectedTimeSlots([]);
                        }}
                      >
                        Cancel
                      </Button>
                      <Button type="submit" disabled={submitting || selectedTimeSlots.length === 0}>
                        {submitting ? "Creating..." : `Create ${selectedTimeSlots.length || ''} Slot(s)`}
                      </Button>
                    </div>
                  </form>
                </DialogContent>
              </Dialog>
            </div>
            </div>

            {loadingSlots ? (
              <div className="text-center py-12 text-muted-foreground">Loading slots...</div>
            ) : slots.length === 0 ? (
              <Card>
                <CardContent className="text-center py-12">
                  <Calendar size={48} className="mx-auto mb-4 opacity-50 text-muted-foreground" />
                  <p className="text-muted-foreground">No availability slots created yet.</p>
                  <p className="text-xs text-muted-foreground mt-2">Click "Add Time Slot" to create one.</p>
                </CardContent>
              </Card>
            ) : (
              <div className="space-y-8">
                {/* Group slots by service first, then by date */}
                {Object.entries(
                  slots.reduce((serviceGroups: Record<string, AvailabilitySlot[]>, slot) => {
                    const serviceKey = slot.serviceId ? `${slot.serviceId}|${slot.serviceName || 'Unknown Service'}` : 'generic|General Availability';
                    if (!serviceGroups[serviceKey]) serviceGroups[serviceKey] = [];
                    serviceGroups[serviceKey].push(slot);
                    return serviceGroups;
                  }, {})
                )
                .sort(([keyA], [keyB]) => {
                  // Sort: generic slots first, then by service name
                  if (keyA.startsWith('generic')) return -1;
                  if (keyB.startsWith('generic')) return 1;
                  return keyA.split('|')[1].localeCompare(keyB.split('|')[1]);
                })
                .map(([serviceKey, serviceSlots]) => {
                  const [, serviceName] = serviceKey.split('|');
                  
                  // Group slots within this service by date
                  const slotsByDate = Object.entries(
                    serviceSlots.reduce((dateGroups: Record<string, AvailabilitySlot[]>, slot) => {
                      const date = slot.slotDate;
                      if (!dateGroups[date]) dateGroups[date] = [];
                      dateGroups[date].push(slot);
                      return dateGroups;
                    }, {})
                  ).sort(([dateA], [dateB]) => new Date(dateA).getTime() - new Date(dateB).getTime());
                  
                  return (
                    <div key={serviceKey} className="space-y-4">
                      <div className="flex items-center gap-3 px-2 py-2 bg-primary/5 rounded-lg border border-primary/20">
                        <DollarSign size={20} className="text-primary" />
                        <h3 className="font-semibold text-lg text-foreground">{serviceName}</h3>
                        <Badge variant="outline" className="ml-auto">
                          {serviceSlots.length} slot{serviceSlots.length !== 1 ? 's' : ''}
                        </Badge>
                      </div>
                      
                      {slotsByDate.map(([date, dateSlots]) => (
                  <div key={date} className="space-y-3">
                    <div className="flex items-center gap-2 px-2">
                      <Calendar size={18} className="text-primary" />
                      <h4 className="font-semibold text-foreground">{formatDate(date)}</h4>
                      <Badge variant="secondary" className="ml-2">
                        {dateSlots.length} slot{dateSlots.length !== 1 ? 's' : ''}
                      </Badge>
                    </div>
                    <div className="grid sm:grid-cols-2 lg:grid-cols-4 gap-3">
                      {dateSlots
                        .sort((a, b) => a.startTime.localeCompare(b.startTime))
                        .map((slot, i) => (
                          <motion.div
                            key={slot.id}
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: i * 0.02 }}
                            className={`rounded-xl border p-4 ${
                              slot.isAvailable
                                ? "bg-green-500/5 border-green-500/20"
                                : "bg-gray-500/5 border-gray-500/20"
                            }`}
                          >
                            <div className="flex items-start justify-between mb-2">
                              <div className="flex-1 space-y-1">
                                {slot.serviceName && (
                                  <div>
                                    <Badge variant="secondary" className="text-xs">
                                      {slot.serviceName}
                                    </Badge>
                                  </div>
                                )}
                                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                                  <Clock size={14} />
                                  <span className="font-medium text-foreground">
                                    {slot.startTime} - {slot.endTime}
                                  </span>
                                </div>
                              </div>
                              <button
                                onClick={() => handleDeleteSlot(slot.id)}
                                className="text-red-400 hover:text-red-300 transition-colors"
                                title="Delete slot"
                              >
                                <Trash2 size={14} />
                              </button>
                            </div>
                            <div className="mt-2">
                              <span
                                className={`text-xs px-2 py-0.5 rounded-full border ${
                                  slot.isAvailable
                                    ? "bg-green-500/10 text-green-400 border-green-500/20"
                                    : "bg-gray-500/10 text-gray-400 border-gray-500/20"
                                }`}
                              >
                                {slot.isAvailable ? "Available" : "Booked"}
                              </span>
                            </div>
                          </motion.div>
                        ))}
                    </div>
                  </div>
                ))
                }
                    </div>
                  );
                })
                }
              </div>
            )}
          </TabsContent>
        </Tabs>
      </div>
    </DashboardLayout>
  );
}
